package java_operator;

public class Assignment {
//=, +=, -=, /=, %=.
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a =4;
		int var;
		var = a;
		System.out.println(var);
		var += a;
		System.out.println(var);
		var -= a;
		System.out.println(var);
		var *= a;
		System.out.println(var);
		var /= a;
		System.out.println(var);
		var %=a;
		System.out.println(var);
		
		

	}

}
